    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed
        String localidad = txtLocalidad.getText().trim();
         idLocalidad = idLocalidad + 1;
         
       Sucursal nuevaSucursal = new Sucursal(idLocalidad, localidad);
    if(!localidad.isEmpty()){
    try {
        File archivo = new File("sucursales.dat");
        FileOutputStream fos = new FileOutputStream(archivo, true);
        ObjectOutputStream oos;

        // Si el archivo ya existe y tiene contenido, se usa una subclase para evitar la cabecera
        if (archivo.length() == 0) {
            oos = new ObjectOutputStream(fos);
        } else {
            oos = new ObjectOutputStream(fos) {
                protected void writeStreamHeader() throws IOException {
                    reset();
                }
            };
        }

        oos.writeObject(nuevaSucursal);
        oos.close();
    } catch (IOException e) {
        e.printStackTrace();
    }
    }else{
        JOptionPane.showMessageDialog(this, "Ingrese una Sucursal");
    }
    }//GEN-LAST:event_btnRegistrarActionPerformed

